import { Button, FormControl, InputLabel, makeStyles, MenuItem, Select, TextField } from '@material-ui/core'
import React, { useState } from 'react'

const useStyles = makeStyles((theme) => ({
    formControl: {
        margin: theme.spacing(1),
        minWidth: 190,
    },
    selectEmpty: {
        marginTop: theme.spacing(2),
    },
}));

const ComposeTeam = () => {
    const classes = useStyles();
    const [age, setAge] = React.useState('');
    let obj = {
        fname: "",
        lname: "",
        height: "",
        position: ""
    };
    const [players, setplayers] = useState(obj);
    const [final, setFinal] = useState([])
    // let players=[];`
    // let position=[]
    const [position,setposition]=useState([])
    const [objPosition,setobjPosition]=useState({
        position:""
    })

    const saveData = () => {
        setFinal(oldarr => [...oldarr, players]);
        setplayers(obj)
    }
    console.log("Players", final);

    const handleChange = (e) => {
        setplayers((prestate) => ({
            ...prestate,
            [e.target.name]: e.target.value,
        }));
        if(e.target.name==="position")
        {
            objPosition.position=e.target.value
        }
        // console.log('objPosition',objPosition);
        // console.log("object ", obj);
    };
    const addPosition=()=>{
        // console.log("addPosition ",objPosition);
        // console.log("Clicked");
        setposition((old)=>({...old,}))
        // console.log('Position',position);
    }
    return (
        <div>
            <TextField onChange={handleChange} value={players.fname} name="fname" id="standard-basic" label="First Name" />
            <br></br>
            <TextField onChange={handleChange} value={players.lname} name="lname" id="standard-basic" label="Last Name" />
            <br></br>
            <TextField onChange={handleChange} value={players.height} name="height" id="standard-basic" label="Height" />
            <br></br>
            <FormControl className={classes.formControl}>
                <InputLabel id="demo-simple-select-label">Position</InputLabel>
                <Select
                    name="position"
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={players.position}
                    onChange={handleChange}
                >
                    <MenuItem value="Point Guard (PG)">Point Guard (PG)</MenuItem>
                    <MenuItem value="Shooting Guard (SG)">Shooting Guard (SG)</MenuItem>
                    <MenuItem value="Small Forward (SF)">Small Forward (SF)</MenuItem>
                    <MenuItem value="Power Forward (PF)">Power Forward (PF)</MenuItem>
                    <MenuItem value="Center (C)">Center (C)</MenuItem>
                </Select>
            </FormControl>
            <lable className="label">(Position can be more then one)</lable>
            <div><Button variant="contained" color="primary" onClick={addPosition}>Add Position</Button>
                <Button variant="contained" color="primary" onClick={saveData}>Save</Button>
            </div>
        </div>
    )
}

export default ComposeTeam
